using Microsoft.AspNetCore.Mvc;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;using Microsoft.Extensions.Configuration;using Stexchange.Data;using Stexchange.Data.Models;using Stexchange.Data.Builders;using static System.String;namespace Stexchange.Controllers{    public class AdvertisementController : Controller    {        public AdvertisementController(Database db, IConfiguration config)        {            Database _databse = db;            IConfiguration _config = config;        }        private Database _database { get; }        private IConfiguration _config { get; }        public IActionResult PostAdvertisement()        {            return View();        }                [HttpPost]        //                                                     TODO!!!!        public async Task<IActionResult> PostAdvertisement(List<byte[]> images, string title, string description,             string name_nl, int quantity, string plant_type, string give_away, string with_pot, string name_lt="",             string light="", string water="", string ph="", string indigenous="")        {            //todo: def val planttype and withpot            try            {                if (ModelState.IsValid)                {                    ListingBuilder listingBuilder = new ListingBuilder();                    List<FilterListing> filterListings = new List<FilterListing>();                    List<Filter> filters = new List<Filter>();                    List<ImageData> imageList = new List<ImageData>();                    Listing finishedListing;                    


                    //TODO: build a listing using the listing builder class 

                    //these are the required properties of the listing being built
                    if (images.First() != null && !IsNullOrEmpty(title)                                             && !IsNullOrEmpty(description)                                             && !IsNullOrEmpty(name_nl)                                             && !IsNullOrEmpty(plant_type)                                            && quantity > 0                                            && !IsNullOrEmpty(give_away))                    {                          listingBuilder.SetProperty("Title", title)                                        .SetProperty("Description", description)                                        .SetProperty("NameNl", name_nl)                                        .SetProperty("Quantity", quantity);                                    filters.Add(new Filter(give_away));                                    filters.Add(new Filter(plant_type));                                    filters.Add(new Filter(with_pot));                        //now the non-required properties                        if (!IsNullOrEmpty(name_lt)) listingBuilder.SetProperty("NameLt", name_lt);                        if (!IsNullOrEmpty(light)) filters.Add(new Filter(light));                        if (!IsNullOrEmpty(water)) filters.Add(new Filter(water));                        if (!IsNullOrEmpty(ph)) filters.Add(new Filter(ph));                        if (!IsNullOrEmpty(indigenous)) filters.Add(new Filter(indigenous));                        //returning the finished listing with listingbuilder .complete()                        finishedListing = listingBuilder.Complete();                        //adding the properties of filterlisting to link the listing to their filters                        foreach (Filter fil in filters)                        {                            //adding new filterlisting instance to filterListings                            filterListings.Add(new FilterListing(finishedListing, fil));                        }                        foreach (var image in images)                        {                            imageList.Add(new ImageData(finishedListing, image));                        }                    }                    

                }            }            catch (Exception e)            {                ViewBag.Error = "Error: " + e.ToString();            }                       //TODO: validate all name values from the form per value if they are empty or wrong and show feedback             //await Database.AddAsync();            //await Database.SaveChangesAsync();            //TODO: input the new tables into the database             //TODO: make validation page in view                      return View(); // todo: insert something here        }    }}